import React, { useState } from "react";
import axios from "axios";
import styles from "./Login.module.css";
import { HiMail as Mail } from "react-icons/hi";
import { RiLockPasswordFill as Pw } from "react-icons/ri";
import { SiGmail as Gm } from "react-icons/si";
import { FaFacebookF as Fb } from "react-icons/fa";

const baseURL = "https://jsonplaceholder.typicode.com/users"; // deploy del back

const initialState = {
    form: {
        mail: "",
        password: "",
    },
};

function Login() {
    const [state, setState] = useState(initialState);
    console.log(state);
    const handleChange = async (e) => {
        await setState({
            form: {
                ...state.form,
                [e.target.name]: e.target.value,
            },
        });
    };
    const login = async () => {
        await axios.get(baseURL, {
            params: {
                mail: state.form.mail,
                password: state.form.password,
            }
                .then((response) => {
                    console.log(response.data);
                })
                .catch((error) => {
                    console.log(error);
                }),
        });
    };

    return (
        <div className={styles.container}>
            <div className={styles.name}>INTOPCOL</div>

            <div className={styles.container2}>
                <div className={styles.formGroup}>
                    <br />
                    <label>
                        <Mail />
                        Correo:
                    </label>
                    <br />
                    <input
                        type="text"
                        className={styles.formControl}
                        name="mail"
                        onChange={handleChange}
                    />
                    <br />
                    <br />
                    <label>
                        <Pw />
                        Contraseña:
                    </label>
                    <br />
                    <input
                        type="password"
                        className={styles.formControl}
                        name="password"
                        onChange={handleChange}
                    />
                    <br />
                    <br />
                    <button className={styles.btnLogin} onClick={() => login}>
                        Iniciar sesión
                    </button>
                    <br />
                    <br />
                    <span>OR</span>
                    <br />
                    <br />
                    <a href="">
                        <Gm /> Ingresa con Gmail
                    </a>
                    <br />
                    <a href="">
                        <Fb /> Ingresa con Facebook
                    </a>
                    <p>¿No tienes una cuenta?</p>
                    <button className={styles.btnLogin}>Regístrate</button>
                    <br />
                </div>
            </div>
        </div>
    );
}

export default Login;
